document.addEventListener("DOMContentLoaded",function(){const e=e=>{e.preventDefault();const t=e.target,n=new FormData(t);t.getAttribute("name");fetch("/",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:new URLSearchParams(n).toString()}).then(()=>document.querySelector(".success").classList.add("active")).catch(e=>alert(e))};document.querySelectorAll("form").forEach(t=>{t.addEventListener("submit",e)})}),window.addEventListener("load",function(){document.body.classList.remove("loading");new Swiper(".swiper",{loop:!1,slidesPerView:"auto",navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},pagination:{el:".swiper-pagination"}})});